function addLongtabListener(target, callback) {
  let timer = 0 // timer

  target.ontouchstart = () => {
    timer = 0 // timer
    timer = setTimeout(() => {
      callback();
      timer = 0
    }, 380) // 
  }

  target.ontouchmove = () => {
    clearTimeout(timer) // 
    timer = 0
  }

  target.ontouchend = () => { // timer380ms
    if (timer) {
      clearTimeout(timer)
    }
  }
}

