function share_() {
  let url = window.location.origin + window.location.pathname
  try {
    // 
    var title = document.title;
    var subTitle = title.endsWith("| MyStory") ? title.substring(0, title.length - 14) : title;
    navigator.clipboard.writeText('MyStory\n' + subTitle + '\n' + url + '\n');
    new Vue({
      data: function () {
        this.$notify({
          title: "",
          message: "",
          position: 'top-left',
          offset: 50,
          showClose: true,
          type: "success",
          duration: 5000
        });
        // return { visible: false }
