/*  start */
var now = new Date();
function createtime() {
  // 
  now.setTime(now.getTime() + 1000);
  // var start = new Date("08/01/2022 00:00:00"); // 1
  var start = new Date("06/01/2025 00:00:00"); // 1
  var dis = Math.trunc(23400000000 + ((now - start) / 1000) * 17); // =* 
  var unit = (dis / 149600000).toFixed(6);  // 
  // 
  var grt = new Date("06/01/2025 00:00:00");
  var days = (now - grt) / 1e3 / 60 / 60 / 24,
    dnum = Math.floor(days),
    hours = (now - grt) / 1e3 / 60 / 60 - 24 * dnum,
    hnum = Math.floor(hours);
  1 == String(hnum).length && (hnum = "0" + hnum);
  var minutes = (now - grt) / 1e3 / 60 - 1440 * dnum - 60 * hnum,
    mnum = Math.floor(minutes);
  1 == String(mnum).length && (mnum = "0" + mnum);
  var seconds = (now - grt) / 1e3 - 86400 * dnum - 3600 * hnum - 60 * mnum,
    snum = Math.round(seconds);
  1 == String(snum).length && (snum = "0" + snum);
  let currentTimeHtml = "";
  (currentTimeHtml =
    hnum < 18 && hnum >= 9
      ? `<img class='boardsign' src='https://lskypro.acozycotage.net/Fomalhaut/badge/F-.svg' title='~'><br> <div style="font-size:13px;font-weight:bold"> ${dnum}  ${hnum}  ${mnum}  ${snum}  <i id="heartbeat" class='fas fa-heartbeat'></i> <br>  1  ${dis}  ${unit}  </div>`
      : `<img class='boardsign' src='https://lskypro.acozycotage.net/Fomalhaut/badge/F-.svg' title='~'><br> <div style="font-size:13px;font-weight:bold"> ${dnum}  ${hnum}  ${mnum}  ${snum}  <i id="heartbeat" class='fas fa-heartbeat'></i> <br>  1  ${dis}  ${unit}  </div>`),
    document.getElementById("workboard") &&
    (document.getElementById("workboard").innerHTML = currentTimeHtml);
}
// 1000ms
setInterval(() => {
  createtime();
}, 1000);

